function hoverShow(){
	$("#products").hover(function(){
		$("#xl1").css("display","block");
			$("#xl1").hover(function(){
			$("#xl1").css("display","block");	
			},function(){
				$("#xl1").css("display","none");
			});
	},function(){
		$("#xl1").css("display","none");
	});

	$("#plan").hover(function(){
		$("#xl2").css("display","block");
			$("#xl2").hover(function(){
			$("#xl2").css("display","block");	
			},function(){
				$("#xl2").css("display","none");
			});
	},function(){
		$("#xl2").css("display","none");
	});

	$("#support").hover(function(){
		$("#xl3").css("display","block");
			$("#xl3").hover(function(){
			$("#xl3").css("display","block");	
			},function(){
				$("#xl3").css("display","none");
			});
	},function(){
		$("#xl3").css("display","none");
	});
}

function height_o(){
	var screenHeight=document.documentElement.clientHeight;
	var screenWidth=document.documentElement.clientWidth;
	var h1=screenHeight-100;
	var w=(screenWidth-1007)/2;
	$(".big-container").height(h1);
	$(".win-container").css("margin-left",w+"px");
	$(".fade").height(screenHeight);
	$(".login-container").height(screenHeight);
	var disTop=(screenHeight-260)/2;
	$(".login").css("top",disTop+"px");	
}



function alertFade(){
	var fade='<div class="fade" id="fade"><div class="fade-main">'+
			'<div class="fade-title">Sales Chat<span id="close"><img src="images/Close.png" width="15px"/></span></div>'+
			'<div class="fade-content"><p>Phone: +65 3100 0088 </p>'+
			'<p>E-mail: sales@iringtel.com</p><p>Skype: iRingTel Sales</p>'+
			'</div><div class="fade-btn"><button class="btn btn-blue" id="ok">OK!</button></div>'+
			'</div></div>';
	$("body").append(fade);
	var screenHeight=document.documentElement.clientHeight;
	var marT=(screenHeight-262)/2;
	$(".fade-main").css("margin-top",marT+"px");
	$("#close").click(function(){
		$("#fade").remove();
	});
	$("#ok").click(function(){
		$("#fade").remove();
	});
}

